/* eslint-disable prettier/prettier */
/* eslint-disable no-console */

import {
    LOGIN_USER_SUCCESS,
    LOGIN_USER_FAIL,
    REGISTER_USER_SUCCESS,
    REGISTER_USER_FAIL,
    LOGOUT_SUCCESS,
    LOGOUT_FAIL,

  } from '../../stores/actions/user.action';

  const initialState = {
    user: {},

  };

  export const userReducer = (state = initialState, action) => {
    console.log(action, initialState);
    switch (action.type) {
      case LOGIN_USER_SUCCESS:
        return {
          ...state,
          user: action.payload,
        };
      case LOGIN_USER_FAIL:
        return {
          ...state,
          errors: true,
        };

      case REGISTER_USER_SUCCESS:
        return {
          ...state,
          user: action.payload,
        };
      case REGISTER_USER_FAIL:
        return {
          ...state,
          errors: true,
        };

      case POST_EVENT_SUCCESS:
        return {
          ...state,
          workEvent: action.payload,
        };
      case POST_EVENT_FAIL:
        return {
          ...state,
          errors: true,
        };

      case POST_WORK_SUCCESS:
        return {
          ...state,
          work: action.payload,
        };
      case POST_WORK1_SUCCESS:
        return {
          ...state,
          work1: action.payload,
        };
      case POST_WORK2_SUCCESS:
        return {
          ...state,
          work2: action.payload,
        };
      case POST_WORK3_SUCCESS:
        return {
          ...state,
          work3: action.payload,
        };
      case POST_WORK4_SUCCESS:
        return {
          ...state,
          work4: action.payload,
        };
      case POST_WORK_FAIL:
        return {
          ...state,
          errors: true,
        };

      case ADD_COLLECTION1_SUCCESS:
        return {
          ...state,
          collection: action.payload,
        };
      case ADD_COLLECTION2_SUCCESS:
        return {
          ...state,
          collection1: action.payload,
        };

        case ADD_WORK_COLLECTION1_SUCCESS:
          return {
            ...state,
            workcollection: action.payload,
          };
        case ADD_WORK_COLLECTION2_SUCCESS:
          return {
            ...state,
            workcollection1: action.payload,
        };

      case DELETE_WORK_COLLECTION1_SUCCESS:
        return {
          ...state,
          workcollection: action.payload,
        };
      case DELETE_WORK_COLLECTION2_SUCCESS:
        return {
          ...state,
          workcollection1: action.payload,
        };

      case LOGOUT_SUCCESS:
        return {
          ...state,
          errors: true,
        };
      case LOGOUT_FAIL:
        return {
          ...state,
          errors: true,
        };
      default:
        return state;
    }
  };

  export default userReducer;
