import * as React from "react";
import "./Matching.css";

import Navbars from "../Navbar/navbarHome2.js";
import Drawer from "../Drawer/Drawer";
import {
  FaLanguage,
  FaFile,
  FaLongArrowAltLeft,
  FaLongArrowAltRight,
} from "react-icons/fa";

import { IconButton, Input, Box, Autocomplete, TextField } from "@mui/material";
import { data } from "../Data/data";
import { MdCameraAlt } from "react-icons/md";

const Matching = () => {
  const [languages, setLanguages] = React.useState("");
  const [languages2, setLanguages2] = React.useState("");
  const [page, setPage] = React.useState("Text");
  const [textarea, setTextarea] = React.useState("");
  const [images, setImages] = React.useState([]);
  const [imageURLs, setImageURLs] = React.useState([]);

  const handleChange = (event) => {
    setTextarea(event.target.value);
  };
  const switchLanguage = () => {
    setLanguages(languages2);
    setLanguages2(languages);
  };

  function onImageChange(e) {
    setImages([...e.target.files]);
  }

  React.useEffect(() => {
    if (images.length < 1) return;
    const newImageUrls = [];
    images.forEach((image) => newImageUrls.push(URL.createObjectURL(image)));
    setImageURLs(newImageUrls);
  }, [images]);

  return (
    <div className="App-body-Order">
      <header className="App-header">
        <Navbars />
      </header>

      <Box sx={{ display: "flex", width: "100% " }}>
        <Drawer />
<>
</>


      </Box>
    </div>
  );
};

export default Matching;
