import * as React from "react";
import "./Home.css";
import Navbars from "../Navbar/navbarHome";
import Footer from "../Footer/Footer";
import { Link } from "react-router-dom";

import { BsArrowRightShort } from "react-icons/bs";

import Online_document from "../../Images/Online_document.png";
import Documents from "../../Images/Documents-amico.png";
import Translator from "../../Images/Translator-bro.png";
import Selecting_team from "../../Images/Selecting_team.png";

const Home = () => {
  const { innerWidth: width, innerHeight: height } = window;
  // const [type, settype] = React.useState(null);
  // const [textarea, setTextarea] = React.useState("");
  // const [file, setFile] = React.useState("");
  // const [promo, setPromo] = React.useState("");

  // const handleChange = (event) => {
  //   setTextarea(event.target.value);
  // };

  // const promotion = (x) => {
  //   setPromo(x);
  //   window.scrollTo(0, 1500);
  // };

  return (
    <>
      <header className="App-header">
        <Navbars />
      </header>

      <div>
        <div className="boxleft">
          <p className="textTranslationHero">Translation Hero</p>
          <p className="textdescription1">
            New options to help you translate
            <br /> the language you want.
          </p>
          <div className="boxleftbutton">
            <button className="buttonGetStart">
              <Link
                to="/Signup"
                style={{
                  color: "#FFFF",
                  fontSize: 18,
                  lineHeight: 21,
                  textDecorationLine: "none",
                }}
              >
                Get Start
              </Link>
            </button>
          </div>
        </div>

        <div className="boxBlue"></div>
        <div className="boxYellow"></div>
        <div className="boxImg1">
          <img
            src={Online_document}
            alt="Online_document"
           className=""
          />
        </div>
      </div>

      <div
        style={{
          position: "absolute",
          top: height * 0.7,
          width: width,
          height: 800,
        }}
      >
        <div
          style={{
            textAlign: "center",
          }}
        >
          <div
            style={{
              fontWeight: "bold",
              fontSize: 45,
              color: "#0097FE",
              marginBottom: 70,
            }}
          >
            <p>Why Us?</p>
          </div>

          <div
            style={{
              width: 350,
              height: 500,
              background: " #FFFFFF",
              border: "1px solid #E5E5E5",
              boxShadow: "0px 4px 20px rgb(0 0 0 / 20%)",
              borderRadius: 20,
              position: "absolute",
              left: 50,
            }}
          >
            <div
              style={{
                textAlign: "center",
              }}
            >
              <img
                src={Documents}
                alt="Documents"
                style={{ width: width * 0.18 }}
              />
            </div>

            <div
              style={{
                position: "absolute",
                width: 280,
                height: 231,
                // left: 124,
                // top: 1029,
                fontStyle: "normal",
                fontWeight: 600,
                fontSize: 20,
                display: "flex",
                alignItems: "center",
                textAlign: "center",
                color: " #333333",
                margin: 30,
                top: 185,
              }}
            >
              <p>
                translate many documents Official documents with more than 50
                forms to choose from, or general translations such as articles,
                movies,thesis, letters, etc.
              </p>
            </div>
          </div>

          <div
            style={{
              width: 350,
              height: 500,
              background: " #FFFFFF",
              border: "1px solid #E5E5E5",
              boxShadow: "0px 4px 20px rgb(0 0 0 / 20%)",
              borderRadius: 20,
              position: "absolute",
              left: 520,
            }}
          >
            <div
              style={{
                textAlign: "center",
              }}
            >
              <img
                src={Translator}
                alt="Translator"
                style={{ width: width * 0.18 }}
              />
            </div>

            <div
              style={{
                position: "absolute",
                width: 280,
                height: 231,
                // left: 124,
                // top: 1029,
                fontStyle: "normal",
                fontWeight: 600,
                fontSize: 20,
                display: "flex",
                alignItems: "center",
                textAlign: "center",
                color: " #333333",
                margin: 30,
                top: 185,
              }}
            >
              <p>Supports translations for more than 15 languages worldwide.</p>
            </div>
          </div>

          <div
            style={{
              width: 350,
              height: 500,
              background: " #FFFFFF",
              border: "1px solid #E5E5E5",
              boxShadow: "0px 4px 20px rgb(0 0 0 / 20%)",
              borderRadius: 20,
              position: "absolute",
              left: 980,
            }}
          >
            <div
              style={{
                textAlign: "center",
              }}
            >
              <img
                src={Selecting_team}
                alt="Selecting_team"
                style={{ width: width * 0.18 }}
              />
            </div>

            <div
              style={{
                position: "absolute",
                width: 280,
                height: 231,
                // left: 124,
                // top: 1029,
                fontStyle: "normal",
                fontWeight: 600,
                fontSize: 20,
                display: "flex",
                alignItems: "center",
                textAlign: "center",
