import * as React from "react";

import Navbars from "../Navbar/navbarLogin";

import { FiEyeOff, FiEye } from "react-icons/fi";

import Login1 from "../../Images/login.png";

import { Formik } from "formik";
import IconButton from "@mui/material/IconButton";
import { BsArrowRightShort, BsArrowLeftShort } from "react-icons/bs";


import { useNavigate, useLocation } from "react-router-dom";
import {
  LOGI01_Box1EN,
  LOGI01_Box1TH,
  LOGI01_Box1DE,
} from "../Data/DataLanguage";

const Login = () => {
  const { innerWidth: width } = window;
  const navigate = useNavigate();
  // const [email, setEmail] = React.useState("");
  // const [password, setPassword] = React.useState("");
  const [type, setType] = React.useState("password");
  const [value, setValue] = React.useState(false);
  const location = useLocation();
  // const pathname = window.location.pathname;

  let Doc = location?.state?.languages;

  const goSignup = (x) => {
    navigate("/Signup", { state: { languages: `${x}` } });
  };

  const goHome = (x) => {
    navigate("/", { state: { languages: `${x}` } });
  };

  const goForgetPassword = (x) => {
    navigate("/ForgetPassword", { state: { languages: `${x}` } });
  };

  const handleClickShowPassword = () => {
    if (type === "password") {
      setValue(!value);
      setType("text");
    } else if (type === "text") {
      setValue(!value);
      setType("password");
    }
  };

  return (
    <>
      <header
        className="App-header"
        style={{ color: "transparent", position: "absolute" }}
      >
        <Navbars />
      </header>

      {Doc === "English" ? (
        <div>
          <div
            style={{
              position: "absolute",
              width: "50%",
              height: " -webkit-fill-available",
              background: "#66C0FE",
            }}
          >
            <img
              src={Login1}
              alt="Login"
              style={{
                width: 460,
                position: "absolute",
                top: " 10%",
                left: 100,
              }}
            />
          </div>

          <div
            style={{
              position: "absolute",
              width: "50%",
              height: "-webkit-fill-available",
              left: "50%",
              background: "#FFF3CC",
            }}
          >
            <div
              style={{
                position: "fixed",
                top: 130,
                left: width * 0.59,
                textAlign: "-webkit-center",
                width: 500,
                background: " #FFFFFF",
                boxShadow: " 0px 4px 25px rgba(0, 0, 0, 0.15)",
                borderRadius: 20,
                padding: 30,
              }}
            >
              <div
                style={{
                  fontWeight: 400,
                  fontSize: 18,
                  textDecorationLine: "none",
                  textAlign: "left",
                  marginBottom: 20,
                  float: "left",
                  background: "transparent",
                  border: "none",
                }}
                onClick={() => goHome("English")}
              >
                <p id={"Hometext"}>{LOGI01_Box1EN[0].label}</p>
                <BsArrowLeftShort id={"Hometext2"} />
              </div>

              <button
                style={{
                  fontWeight: 400,
                  textAlign: "right",
                  marginBottom: 20,
                  float: "right",
                  color: "#D9D9D9",
                  background: "transparent",
                  border: "none",
                }}
                onClick={() => goSignup("English")}
              >
