import * as React from "react";
import { Box } from "@mui/material";
import Drawer from "../Drawer/DrawerTranslate";
import Navbars from "../Navbar/navbarTanslater";
import { Editor } from "@tinymce/tinymce-react";

export default function Tool() {
  const editorRef = React.useRef();
  const { innerWidth: width } = window;

  return (
    <div className="App-body3">
      <header className="App-header">
        <Navbars />
      </header>
      <Box sx={{ display: "flex", width: "100% " }}>
        <Drawer />
        <Box component="main">
          <div style={{ marginTop: 100, marginLeft: 21, width: width * 0.9 }}>
            <p
              style={{
                textAlign: "left",

                fontSize: 30,
                fontWeight: 500,
                float: "left",
              }}
            >
              Tool
            </p>
            <div style={{ float: "none", marginBottom: 30, textAlign: "end" }}>
              <button
                style={{
                  background: "#F6FBFE",
                  border: "none",
                  width: 200,
                  height: 50,

                }}
              >
                helper
              </button>
              <button
                style={{
                  background: "#FFDB66",
                  border: "none",
                  width: 200,
                  height: 50,
                }}
              >
                Submit work
              </button>
            </div>
            <Editor
              onInit={(evt, editor) => (editorRef.current = editor)}
              init={{
                height: 560,
              }}
            />
          </div>
        </Box>
      </Box>
    </div>
  );
}
