import * as React from "react";

// import Navbars from "../Navbar/navbarSignup";
import {
  FaFacebookF,
  FaLinkedinIn,
  FaUserAlt,
  FaAddressCard,
} from "react-icons/fa";
import { BsInstagram, BsTwitter } from "react-icons/bs";
import { FiEyeOff, FiEye, FiImage } from "react-icons/fi";
import { MdCameraAlt } from "react-icons/md";
import { Link } from "react-router-dom";






const Signup_company = () => {

  const { innerWidth: width, innerHeight: height } = window;
  const [file, setFile] = React.useState("");
  const [firstname, setFirstname] = React.useState("");
  const [lastname, setLastname] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [confirmpassword, setConfirmpassword] = React.useState("");
  const [mobilephone, setMobilephone] = React.useState("");
  const [screen, setScreen] = React.useState(1);
  const [companyname, setCompanyname] = React.useState("");
  const [personnumber, setPersonnumber] = React.useState("");
  const [cmpanyaddress, setCompanyaddress] = React.useState("");
  const [district, setDistrict] = React.useState("");
  const [country, setCountry] = React.useState("");
  const [province, setProvince] = React.useState("");
  const [zipcode, setZipcode] = React.useState("");
  const [type2, setType2] = React.useState("password");
  const [website, setWebsite] = React.useState("");

  const [type, setType] = React.useState("password");
  const [values, setValues] = React.useState(false);
  const [values2, setValues2] = React.useState(false);

  const handleClickShowPassword = (i) => {
    if (i === 1) {
      if (type === "password") {
        setValues(!values);
        setType("text");
      } else if (type === "text") {
        setValues(!values);
        setType("password");
      }
    } else if (i === 2) {
      if (type === "password") {
        setValues2(!values2);
        setType("text");
      } else if (type === "text") {
        setValues2(!values2);
        setType("password");
      }
    }
  };





  return (
    <>
      <div>
                <div
                  style={{
                    position: "fixed",
                    top: 20,
                    left: width * 0.59,
                    textAlign: "-webkit-center",
                    width: 500,
                    background: " #FFFFFF",
                    boxShadow: " 0px 4px 25px rgba(0, 0, 0, 0.15)",
                    borderRadius: 20,
                    padding: 30,
                  }}
                >
                  <div
                    style={{
                      fontWeight: 400,
                      fontSize: 18,
                      textDecorationLine: "none",
                      color: "#D9D9D9",
                      textAlign: "left",
                      marginBottom: 20,
                      float: "left",
                    }}
                  >
                    <BsArrowLeftShort />
                    <Link
                      to="/"
                      style={{
                        color: "#D9D9D9",
                        fontSize: 18,
                        textDecorationLine: "none",
                      }}
                    >
                      Home
                    </Link>
                  </div>
                  <div
                    style={{
                      fontWeight: 400,
                      textAlign: "right",
                      marginBottom: 20,
                      float: "right",
                      color: "#D9D9D9",
                    }}
                  >
                    <Link
                      to="/Login"
                      style={{
                        color: "#D9D9D9",
                        fontSize: 18,
                        textDecorationLine: "none",
                      }}
                    >
                      Login
                    </Link>
                    <BsArrowRightShort />
                  </div>

                  <h2 className="textSignup">Sign up</h2>

                  <div>
                    <FormControl style={{ display: "inherit" }}>
                      <RadioGroup
                        row
                        aria-labelledby="demo-row-radio-buttons-group-label"
                        name="row-radio-buttons-group"
                      >
                        <FormControlLabel
                          onChange={handleChange}
                          name="radio-buttons"
                          value=""
                          control={<Radio />}
                          label="For Customers"
                          style={{ marginRight: 50 }}
                        />
                        <FormControlLabel
                          checked={true}
                          onChange={handleChange}
                          name="radio-buttons"
                          value="For Translators"
                          control={<Radio />}
                          label="For Translators"
                        />
                      </RadioGroup>
                    </FormControl>
                  </div>

                  <div style={{ textAlign: "left", marginTop: 10 }}>
                    <p
                      style={{
                        fontWeight: 500,
                        fontSize: 15,
                        color: "#242424",
                      }}
                    >
                      Name
                    </p>
                    <input
                      type="text"
                      value={firstname}
                      onChange={(e) => setFirstname(e.target.value)}
                      placeholder="Name"
                      style={{
                        background: "#FFFFFF",
                        border: "1px solid #F1F1F1 ",
                        borderRadius: 20,
                        width: "100%",
                        height: 30,
                        padding: 20,
                        paddingLeft: 12,
                        margin: 10,
                        fontSize: 13,
                      }}
                    />
                  </div>

                  <div style={{ textAlign: "left" }}>
                    <p
                      style={{
                        fontWeight: 500,
                        fontSize: 15,
                        color: "#242424",
                      }}
                    >
                      E-mail
                    </p>
                    <input
                      type="text"
                      name="user_email"
                      // value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Example@mail.com"
                      style={{
                        background: "#FFFFFF",
                        border: "1px solid #F1F1F1",
                        borderRadius: 20,
                        width: "100%",
                        height: 30,
                        padding: 20,
                        paddingLeft: 12,
                        margin: 10,
                        fontSize: 13,
                      }}
                    />
                  </div>

                  <div style={{ textAlign: "left" }}>
                    <p
                      style={{
                        fontWeight: 500,
                        fontSize: 15,
                        color: "#242424",
                      }}
                    >
                      Password
                    </p>

                    <input
                      type={type}
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      placeholder="Enter your password"
                      style={{
                        background: "#FFFFFF",
                        border: "1px solid #F1F1F1 ",
                        borderRadius: 20,
                        borderColor: "#F1F1F1",
                        width: "100%",
                        height: 30,
                        padding: 20,
                        paddingLeft: 12,
                        margin: 10,
                        fontSize: 13,
                      }}
                    />
                    <IconButton
                      onClick={() => handleClickShowPassword(1)}
                      edge="end"
                      style={{ position: "absolute", right: 40, top: 380 }}
                    >
                      {values ? <FiEye /> : <FiEyeOff />}
                    </IconButton>
                  </div>

                  <div style={{ textAlign: "left" }}>
                    <p
                      style={{
                        fontWeight: 500,
                        fontSize: 15,
                        color: "#242424",
                      }}
                    >
                      Confirm password
                    </p>

                    <input
                      type={type2}
                      value={confirmpassword}
                      onChange={(e) => setConfirmpassword(e.target.value)}
                      placeholder="Enter your password"
                      style={{
                        background: "#FFFFFF",
                        border: "1px solid #F1F1F1 ",
                        borderRadius: 20,
                        borderColor: "#F1F1F1",
                        width: "100%",
                        height: 30,
                        padding: 20,
                        paddingLeft: 12,
                        margin: 10,
                        fontSize: 13,
                      }}
                    />
                    <IconButton
                      onClick={() => handleClickShowPassword(2)}
                      edge="end"
                      style={{ position: "absolute", right: 40, top: 465 }}
                    >
                      {values2 ? <FiEye /> : <FiEyeOff />}
                    </IconButton>
                  </div>

                  <div style={{ textAlign: "left", marginBottom: 10 }}>
                    <p
                      style={{
                        fontWeight: 500,
                        fontSize: 15,
                        color: "#242424",
                      }}
                    >
                      Mobile phone
                    </p>

                    <input
                      type="text"
                      value={mobilephone}
                      onChange={(e) => setMobilephone(e.target.value)}
                      placeholder="Mobile phone"
                      style={{
                        background: "#FFFFFF",
                        border: "1px solid #F1F1F1 ",
                        borderRadius: 20,
                        borderColor: "#F1F1F1",
                        width: "100%",
                        height: 30,
                        padding: 20,
                        paddingLeft: 12,
                        margin: 10,
                        fontSize: 13,
                      }}
                    />
                  </div>

                  <button
                    style={{
                      width: "100%",
                      borderRadius: 20,
                      background: " #001E33",
                      height: 40,
                      color: "#FFFFFF",
                      fontSize: 18,
                      borderColor: "transparent",
                    }}
                    onClick={() => setScreen("4")}
                  >
                    Next
                  </button>
                </div>
              </div>

    </>
  );
};
export default Signup_company;
