import * as React from "react";
import "./Matching.css";

import Navbars from "../Navbar/navbarHome2.js";
import Drawer from "../Drawer/Drawer";
import {
  FaLanguage,
  FaFile,
  FaLongArrowAltLeft,
  FaLongArrowAltRight,
} from "react-icons/fa";
// import { MdArrowDropDown } from "react-icons/md";

import { Box, Autocomplete, TextField } from "@mui/material";
import { data } from "../Data/data";

const Matching = () => {
  const [languages, setLanguages] = React.useState("");
  const [languages2, setLanguages2] = React.useState("");
  const [page, setPage] = React.useState("Text");
  const [textarea, setTextarea] = React.useState("");
  const [images, setImages] = React.useState([]);
  const [imageURLs, setImageURLs] = React.useState([]);

  const handleChange = (event) => {
    setTextarea(event.target.value);
  };
  const switchLanguage = () => {
    setLanguages(languages2);
    setLanguages2(languages);
  };
  return (
    <div className="App-body-Order">
      <header className="App-header">
        <Navbars />
      </header>

      <Box sx={{ display: "flex", width: "100% " }}>
        <Drawer />

        <div style={{ marginTop: 60, height: 100 }}>
          <h4
            style={{
              fontWeight: 500,
              fontSize: 36,
              color: " #333333",
              marginLeft: 20,
              position: "absolute",
              top: "13%",
            }}
          >
            Translate
          </h4>
        </div>

        <div className="bodyMatching">
          <div style={{ margin: 30 }}>
            <p
              style={{
                textAlign: "start",
                color: "#034D82",
                fontWeight: 600,
                fontSize: 24,
              }}
            >
              Article
            </p>
            <div style={{ textAlign: "start" }}>
              <button
                className="buttonMatching"
                onClick={() => setPage("Text")}
              >
                <FaLanguage className="iconsMatching" />
                <p className="textMatching">Text</p>
              </button>
              <button
                className="buttonMatching"
                onClick={() => setPage("File")}
              >
                <FaFile className="iconsMatching" />
                <p className="textMatching">File</p>
              </button>
            </div>
            {page === "Text" ? (
              <div>
                <div style={{ float: "left" }}>
                  <p
                    style={{
                      textAlign: "start",
                      color: "#353535",
                      fontSize: 18,
                      marginBottom: 20,
                    }}
                  >
                    language
                  </p>
                  <Autocomplete
                    disablePortal
                    id="combo-box-demo"
                    options={data}
                    sx={{ width: 582 }}
                    renderInput={(params) => (
                      <TextField {...params} label="language" />
                    )}
                    value={languages}
                    onChange={(event, value) => setLanguages(value?.label)}
                  />
                </div>

                <button
                  style={{
                    float: "left",
                    width: 30,
                    top: 60,
                    position: "relative",
                    fontSize: 30,
                    marginLeft: 20,
                    marginRight: 20,
                    background: "transparent",
                    border: "none",
                    color: "#262DBB",
                  }}
                  onClick={() => switchLanguage()}
                >
                  <FaLongArrowAltRight style={{ position: "absolute" }} />

                  <FaLongArrowAltLeft style={{ position: "relative" }} />
                </button>

                <div style={{ float: "left" }}>
                  <p
                    style={{
                      textAlign: "start",
                      color: "#353535",
                      fontSize: 18,
                      marginBottom: 20,
                    }}
                  >
                    language
                  </p>
                  <Autocomplete
                    disablePortal
                    id="combo-box-demo"
                    options={data}
                    sx={{ width: 582 }}
                    renderInput={(params) => (
                      <TextField {...params} label="language" />
                    )}
                    value={languages2}
                    onChange={(event, value) => setLanguages2(value?.label)}
                  />
                </div>

                <div>
                  <textarea
                    value={textarea}
                    onChange={handleChange}
                    maxLength={350}
                    style={{
                      position: "relative",
                      width: "100%",
                      height: 300,
                      background: "#FFFFFF",
                      border: "1px solid #E5E5E5",
                      boxSizing: "border-box",
                      borderRadius: 5,
                      padding: 20,
                      top: 30,
                      fontSize: 25,
                    }}
                    placeholder="Text"
                  />
                </div>

                <div style={{ marginTop: 38 }}>
                  <div style={{ float: "left" }}>
                    <button
                      style={{
                        width: 100,
                        height: 40,
                        background: "transparent",
                        color: "#CF0202",
                        border: "1px solid #CF0202",
                        borderRadius: 5,
                      }}
                    >
                      Cancle
                    </button>
                  </div>
                  <div style={{ textAlign: "end" }}>
                    <button
                      style={{
                        width: 200,
                        height: 40,
                        background: "#047ACF",
                        color: "#FFFFFF",
                        border: "none",
                        borderRadius: 5,
                      }}
                    >
                      Find a translator
                    </button>
                  </div>
                </div>
              </div>
            ) : (
              <div>
                <p style={{ fontSize: 25, fontWeight: 800,marginBottom:20 }}>select document</p>
                <p style={{ fontSize: 25, fontWeight: 800,marginBottom:20 }}>Upload a .docx, .pdf, .pptx, or .xlsx file.</p>
                {images.length < 1 ? (
                  <div
                    style={{
                      background: "#FFFFFF",
                      borderRadius: 100,
                      width: 83,
                      height: 80,
                      padding: 15,
                      position: "relative",
                      borderStyle: " dotted ",
                      borderColor: "#0865A8",
                      borderWidth: 2,
                      margin: 25,
                    }}
                  >
                    <label htmlFor="icon-button-file">
                      <Input
                        accept="image/*"
                        id="icon-button-file"
                        type="file"
                        style={{ display: "none" }}
                        onChange={onImageChange}
                      />
                      <IconButton
                        color="primary"
                        aria-label="upload picture"
                        component="span"
                      >
                        <MdCameraAlt
                          style={{ fontSize: 35, position: "absolute" }}
                        />
                        <p
                          style={{
                            fontSize: 8,
                            position: "absolute",
                            top: 20,
                            width: 63,
                          }}
                        >
                          Upload File
                        </p>
                      </IconButton>
                    </label>
                  </div>
                ) : (
                  <div
                    style={{
                      borderRadius: 100,
                      width: 100,
                      height: 100,
                      position: "relative",
                      borderWidth: 2,
                      margin: 25,
                    }}
                  >
                    <label htmlFor="icon-button-file">
                      <Input
                        accept="image/*"
                        id="icon-button-file"
                        type="file"
                        style={{ display: "none" }}
                        onChange={onImageChange}
                      />
                      <IconButton
                        color="primary"
                        aria-label="upload picture"
                        component="span"
                      >
                        {imageURLs.map((imageSrc, idx) => (
                          <img
                            key={idx}
                            src={imageSrc}
                            alt="imageURLs"
                            style={{
                              position: "absolute",
                              width: 90,
                              height: 90,
                              borderRadius: "100%",
                              top: -20,
                            }}
                          />
                        ))}
                      </IconButton>
                    </label>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </Box>
    </div>
  );
};

export default Matching;
